#include <stdlib.h>
#include <stdio.h>
#include <stdbool.h>
#include <math.h>
#include <gmp.h>

int main()
{
  mpz_t num, sum, r;
  mpz_init(sum);
  mpz_init(r);
  mpz_init_set_str(num, "", 2);

  while(mpz_cmp_ui(num, 0) > 0)
  {
    mpz_fdiv_r_ui(r, num, 10);

    mpz_add(sum, sum, r);
    mpz_fdiv_q_ui(num, num, 10);
  }

  gmp_printf("%Zd\n", sum);
  return 0;
}